import React, { useEffect, useState } from "react";
import Header from "./../components/Header";
import Rating from "../components/homeComponents/Rating";
import { Link } from "react-router-dom";
import Message from "./../components/LoadingError/Error";
import { useDispatch, useSelector } from "react-redux";
import { listProductDetails } from "../Redux/Actions/ProductActions";
import Loading from "../components/LoadingError/Loading";

const SingleProduct = ({ history, match }) => {
  const [qty, setQty] = useState(1);
  const dispatch = useDispatch();
  const productID = match.params.id;
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;
  useEffect(() => {
    dispatch(listProductDetails(productID));
  }, [dispatch, productID]);

  const AddToCartHandle = (e) => {
    e.preventDefault();
    history.push(`/cart/${productID}?qty=${qty}`);
  };
  return (
    <>
      <Header />
      <div className="container single-product">
        {loading ? (
          <Loading />
        ) : error ? (
          <Message variant="alert-danger">{error}</Message>
        ) : (
          <>
            <div className="row">
              <div className="col-md-6">
                <div className="single-image">
                  <img src={product.image} alt={product.name} />
                </div>
              </div>
              <div className="col-md-6">
                <div className="product-dtl">
                  <div className="product-info">
                    <div className="product-name">{product.name}</div>
                  </div>
                  <p>{product.description}</p>

                  <div className="product-count col-lg-7 ">
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Giá : </h6>
                      <span>${product.price}</span>
                    </div>
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Status</h6>
                      {product.countInStock > 0 ? (
                        <span>Trong Kho</span>
                      ) : (
                        <span>unavailable</span>
                      )}
                    </div>
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Đánh giá :</h6>
                      <Rating
                        value={product.rating}
                        text={`${product.numReviews} reviews`}
                      />
                    </div>
                    {product.countInStock > 0 ? (
                      <>
                        <div className="flex-box d-flex justify-content-between align-items-center">
                          <h6>Số Lương :</h6>
                          <select>
                            {[...Array(product.countInStock).keys()].map(
                              (x) => (
                                <option key={x + 1} value={x + 1}>
                                  {x + 1}
                                </option>
                              )
                            )}
                          </select>
                        </div>
                        <button
                          onClick={AddToCartHandle}
                          className="round-black-btn"
                        >
                          Thêm vào giỏ hàng
                        </button>
                      </>
                    ) : null}
                  </div>
                </div>
              </div>
            </div>

            {/* RATING */}
            <div className="row my-5">
              <div className="col-md-6">
                <h6 className="mb-3">ĐÁNH GIÁ</h6>
                <Message variant={"alert-info mt-3"}>Không có gì cả</Message>
                <div className="mb-5 mb-md-3 bg-light p-3 shadow-sm rounded">
                  <strong>Công Nghệ Web</strong>
                  <Rating />
                  <span>12 tháng 4 2022</span>
                  <div className="alert alert-info mt-3">
                    Dâu tây vườn hay gọi đơn giản là dâu tây (danh pháp khoa
                    học: Fragaria × ananassa)[1] là một chi thực vật hạt kín và
                    loài thực vật có hoa thuộc họ Hoa hồng (Rosaceae). Dâu tây
                    xuất xứ từ châu Mỹ và được các nhà làm vườn châu Âu cho lai
                    tạo vào thế kỷ 18 để tạo nên giống dâu tây được trồng rộng
                    rãi hiện nay
                  </div>
                </div>
              </div>
              <div className="col-md-6">
                <h6>VIẾT ĐÁNH GÍA </h6>
                <div className="my-4"></div>

                <form>
                  <div className="my-4">
                    <strong>Đánh giá</strong>
                    <select className="col-12 bg-light p-3 mt-2 border-0 rounded">
                      <option value="">Chọn đi...</option>
                      <option value="1">1 - Không ngon</option>
                      <option value="2">2 - Bình Thường</option>
                      <option value="3">3 - Ngon</option>
                      <option value="4">4 - Rất Ngon</option>
                      <option value="5">5 - Tuyệt vời</option>
                    </select>
                  </div>
                  <div className="my-4">
                    <strong>Bình Luận</strong>
                    <textarea
                      row="3"
                      className="col-12 bg-light p-3 mt-2 border-0 rounded"
                    ></textarea>
                  </div>
                  <div className="my-3">
                    <button className="col-12 bg-black border-0 p-3 rounded text-white">
                      GỬI ĐI
                    </button>
                  </div>
                </form>
                <div className="my-3">
                  <Message variant={"alert-warning"}>
                    Làm ơn{" "}
                    <Link to="/login">
                      " <strong>Đăng Nhập</strong> "
                    </Link>{" "}
                    để tham gia bình luận{" "}
                  </Message>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </>
  );
};

export default SingleProduct;
